import React, { useState, useEffect } from 'react';
import './styles/App.css';
import SearchBar from './components/SearchBar';
import ResultsList from './components/ResultsList';
import LoadingSpinner from './components/LoadingSpinner';
import AdvancedSearchForm from './components/AdvancedSearchForm';
import Pagination from './components/Pagination';
import KnowledgeGraph from './components/KnowledgeGraph';
import { searchPubMed, fetchKnowledgeGraph } from './services/api';

const RESULTS_PER_PAGE = 10;

function App() {
  const [allResults, setAllResults] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [hasSearched, setHasSearched] = useState(false);
  const [searchMode, setSearchMode] = useState('simple');
  const [suggestion, setSuggestion] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [simpleQuery, setSimpleQuery] = useState('');
  const [lastQuery, setLastQuery] = useState('');
  const [viewMode, setViewMode] = useState('list');
  const [graphData, setGraphData] = useState({ nodes: [], links: [] });
  const [isGraphLoading, setIsGraphLoading] = useState(false);

  const handleSearch = async (query) => {
    if (!query || (typeof query === 'string' && query.trim() === '')) return;
    setIsLoading(true);
    setError('');
    setHasSearched(true);
    setAllResults([]);
    setSuggestion('');
    setCurrentPage(1);
    setLastQuery(query);
    setViewMode('list');

    try {
      const data = await searchPubMed(query, searchMode);
      setAllResults(data.results);
      setSuggestion(data.suggestion || '');
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    const getGraphData = async () => {
      if (viewMode === 'graph' && lastQuery && !isLoading) {
        setIsGraphLoading(true);
        setGraphData({ nodes: [], links: [] });
        try {
          const data = await fetchKnowledgeGraph(lastQuery);
          setGraphData(data);
        } catch (err) {
          setError(err.message);
        } finally {
          setIsGraphLoading(false);
        }
      }
    };
    getGraphData();
  }, [viewMode, lastQuery, isLoading]);

  const handleSuggestionClick = (suggestedQuery) => {
    setSimpleQuery(suggestedQuery);
    handleSearch(suggestedQuery);
  };

  const lastResultIndex = currentPage * RESULTS_PER_PAGE;
  const firstResultIndex = lastResultIndex - RESULTS_PER_PAGE;
  const currentResults = allResults.slice(firstResultIndex, lastResultIndex);
  const totalPages = Math.ceil(allResults.length / RESULTS_PER_PAGE);

  // --- REFINED Rendering Logic ---
  const renderContent = () => {
    if (isLoading) {
      return <LoadingSpinner />;
    }
    if (error) {
      return <div className="error-message">{error}</div>;
    }
    if (!hasSearched) {
      return null; // Don't show anything before the first search
    }
    if (viewMode === 'graph') {
      return isGraphLoading ? <LoadingSpinner /> : <KnowledgeGraph graphData={graphData} />;
    }
    if (allResults.length > 0) {
      return (
        <>
          <ResultsList results={currentResults} />
          <Pagination currentPage={currentPage} totalPages={totalPages} onPageChange={setCurrentPage} />
        </>
      );
    }
    return <div className="info-message">No results found for this query.</div>;
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>PubMed Semantic Search</h1>
        <p>Your AI-powered research assistant</p>
      </header>
      
      <div className="search-mode-toggle">
        <button className={searchMode === 'simple' ? 'active' : ''} onClick={() => setSearchMode('simple')}>Simple</button>
        <button className={searchMode === 'advanced' ? 'active' : ''} onClick={() => setSearchMode('advanced')}>Advanced</button>
      </div>
      
      {searchMode === 'simple' ? (
        <SearchBar onSearch={handleSearch} isLoading={isLoading} query={simpleQuery} setQuery={setSimpleQuery} />
      ) : (
        <AdvancedSearchForm onSearch={handleSearch} isLoading={isLoading} />
      )}
      
      {!isLoading && suggestion && <div className="suggestion-container">Did you mean: <span className="suggestion-link" onClick={() => handleSuggestionClick(suggestion)}>{suggestion}</span>?</div>}
      
      {hasSearched && !isLoading && !error && (
        <div className="view-mode-toggle">
          <button className={viewMode === 'list' ? 'active' : ''} onClick={() => setViewMode('list')}>List View</button>
          <button className={viewMode === 'graph' ? 'active' : ''} onClick={() => setViewMode('graph')}>Graph View</button>
        </div>
      )}

      <div className="content-area">
        {renderContent()}
      </div>
    </div>
  );
}

export default App;
