import React, { useRef, useCallback } from 'react';
import ForceGraph2D from 'react-force-graph-2d';

const KnowledgeGraph = ({ graphData }) => {
  const fgRef = useRef();

  // --- NEW: Custom node rendering function ---
  const handleNodeCanvasObject = (node, ctx, globalScale) => {
    const label = node.label;
    const fontSize = 12 / globalScale; // Make font size responsive to zoom
    ctx.font = `${fontSize}px Sans-Serif`;

    // Draw the node circle
    ctx.beginPath();
    ctx.arc(node.x, node.y, 5, 0, 2 * Math.PI, false);
    ctx.fillStyle = node.color || 'lightblue'; // Use the color from the graph or a default
    ctx.fill();

    // Draw the node label
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillStyle = 'black'; // Label color
    ctx.fillText(label, node.x, node.y + 10); // Position label slightly below the node
  };

  const handleNodeClick = useCallback(node => {
    // Aim at node from outside it to zoom and center on it
    const distance = 40;
    const distRatio = 1 + distance / Math.hypot(node.x, node.y, node.z);

    if (fgRef.current) {
        fgRef.current.cameraPosition(
          { x: node.x * distRatio, y: node.y * distRatio, z: node.z * distRatio }, // new position
          node, // lookAt ({ x, y, z })
          3000  // ms transition duration
        );
    }
  }, [fgRef]);

  if (!graphData || !graphData.nodes || graphData.nodes.length === 0) {
    return <div className="info-message">No graph data could be generated for this query.</div>;
  }

  return (
    <div className="graph-container">
      <ForceGraph2D
        ref={fgRef}
        graphData={graphData}
        
        // --- UPDATED AND NEW PROPERTIES ---
        nodeCanvasObject={handleNodeCanvasObject} // Use our custom drawing function instead of nodeLabel
        nodeVal={8} // Makes nodes slightly larger and easier to click
        nodeAutoColorBy="group" // Keep color coding by entity type
        
        linkDirectionalArrowLength={3.5}
        linkDirectionalArrowRelPos={1}
        linkLabel="label" // Show relationship label on hover
        linkWidth={1}
        
        onNodeClick={handleNodeClick}
      />
    </div>
  );
};

export default KnowledgeGraph;
